// App is our parent component and is going to have CardList as it's child. 
import React,{Component} from 'react';
import Cardlist from './Cardlist.js';
import Searchbox from './Searchbox.js';
import {robots} from './robots.js'
 var filtered_robots=robots;
class App extends Component{
	constructor(){
		super();
		this.state={
			robots:robots,
			searchField:''
		}
	}
	searchChanges=(event)=>{
		this.setState({searchField:event.target.value});
		filtered_robots=this.state.robots.filter(robots=>{
			return robots.name.toLowerCase().includes(this.state.searchField.toLowerCase());
		})
		console.log(event.target.value);
	}
	render(){
		return(
			<div className='tc'>
				<h1>RoboFriends</h1>
				<Searchbox searchChange={this.searchChanges}/>
				<Cardlist robots='filtered_robots'/>
			</div>
		);
	}

}
export default App;
